    chisq.test, fisher.test

survey_b |&gt;
  tabyl(state, species)

 state Fgra Fspp
    PR  150   66
    RS  402   48

library(janitor)
survey_b |&gt;
  tabyl(species, year)

 species 2009 2010 2011
    Fgra  225  187  140
    Fspp   40   29   45

library(ggplot2)

survey_b |&gt;
  group_by(year, species) |&gt;
  count() |&gt;
  ggplot(aes(year, n))







survey_b |&gt;
  group_by(year, species) |&gt;
  count() |&gt;
  ggplot(aes(year, n, fill = species, color= species))+
  geom_col()+
  scale_fill_viridis_d()

